var _objectSpread = require("D:\\usyd\\2023 season 1\\5425\\react-tesseract-ocr-master\\node_modules\\babel-preset-react-app\\node_modules\\@babel\\runtime/helpers/objectSpread");
/**
 * In the recognition result of tesseract, there
 * is a deep JSON object for details, it has around
 *
 * The result of dump.js is a big JSON tree
 * which can be easily serialized (for instance
 * to be sent from a webworker to the main app
 * or through Node's IPC), but we want
 * a (circular) DOM-like interface for walking
 * through the data.
 *
 * @fileoverview DOM-like interface for walking through data
 * @author Kevin Kwok <antimatter15@gmail.com>
 * @author Guillermo Webster <gui@mit.edu>
 * @author Jerome Wu <jeromewus@gmail.com>
 */

module.exports = function (page) {
  var blocks = [];
  var paragraphs = [];
  var lines = [];
  var words = [];
  var symbols = [];
  page.blocks.forEach(function (block) {
    block.paragraphs.forEach(function (paragraph) {
      paragraph.lines.forEach(function (line) {
        line.words.forEach(function (word) {
          word.symbols.forEach(function (sym) {
            symbols.push(_objectSpread({}, sym, {
              page: page,
              block: block,
              paragraph: paragraph,
              line: line,
              word: word
            }));
          });
          words.push(_objectSpread({}, word, {
            page: page,
            block: block,
            paragraph: paragraph,
            line: line
          }));
        });
        lines.push(_objectSpread({}, line, {
          page: page,
          block: block,
          paragraph: paragraph
        }));
      });
      paragraphs.push(_objectSpread({}, paragraph, {
        page: page,
        block: block
      }));
    });
    blocks.push(_objectSpread({}, block, {
      page: page
    }));
  });
  return _objectSpread({}, page, {
    blocks: blocks,
    paragraphs: paragraphs,
    lines: lines,
    words: words,
    symbols: symbols
  });
};
